import React, { useEffect, useRef } from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import { Chart, PieController, ArcElement, Tooltip, Legend } from 'https://cdn.jsdelivr.net/npm/chart.js@3.7.0/+esm';

function getMockValues() {
  return [33, 33, 34]; // ÐŸÐ¾Ñ‡Ñ‚Ð¸ Ñ€Ð°Ð²Ð½Ð¾Ð¼ÐµÑ€Ð½Ð¾ â€” ÐºÑ€Ð°ÑÐ¸Ð²Ð¾ ÑÐ¼Ð¾Ñ‚Ñ€Ð¸Ñ‚ÑÑ
}

function Dashboard() {
  const chartRef = useRef(null);

  useEffect(() => {
    Chart.register(PieController, ArcElement, Tooltip, Legend);

    const ctx = chartRef.current.getContext('2d');

    // Ð¡Ð¾Ð·Ð´Ð°Ð½Ð¸Ðµ Ð³Ñ€Ð°Ð´Ð¸ÐµÐ½Ñ‚Ð¾Ð²
    const gradient1 = ctx.createLinearGradient(0, 0, 300, 0);
    gradient1.addColorStop(0, '#00f2fe');
    gradient1.addColorStop(1, '#4facfe');

    const gradient2 = ctx.createLinearGradient(0, 0, 0, 300);
    gradient2.addColorStop(0, '#43e97b');
    gradient2.addColorStop(1, '#38f9d7');

    const gradient3 = ctx.createLinearGradient(0, 0, 300, 300);
    gradient3.addColorStop(0, '#f9d423');
    gradient3.addColorStop(1, '#ff4e50');

    const data = {
      labels: ['ðŸ”¥ ÐŸÑ€Ð¾Ð´ÑƒÐºÑ‚Ð¸Ð²Ð½Ð¾ÑÑ‚ÑŒ', 'ðŸš€ Ð­Ð½ÐµÑ€Ð³Ð¸Ñ', 'ðŸŽ¯ Ð¤Ð¾ÐºÑƒÑ'],
      datasets: [{
        data: getMockValues(),
        backgroundColor: [gradient3, gradient2, gradient1],
        borderWidth: 4,
        borderColor: '#ffffff',
        hoverOffset: 12,
      }]
    };

    const myChart = new Chart(ctx, {
      type: 'pie',
      data: data,
      options: {
        responsive: true,
        maintainAspectRatio: true,
        plugins: {
          legend: {
            position: 'bottom',
            labels: {
              font: {
                size: 14,
                family: "'Segoe UI', sans-serif"
              },
              boxWidth: 20,
              padding: 20,
              color: '#333'
            }
          },
          tooltip: {
            callbacks: {
              label: (tooltipItem) => {
                return ` ${tooltipItem.label}: ${tooltipItem.raw}%`;
              }
            }
          }
        },
        animation: {
          animateScale: true,
          animateRotate: true
        }
      }
    });

    return () => myChart.destroy();
  }, []);

  return (
    <div id="chartContainer" style={{ width: '100%', height: '100%', position: 'relative' }}>
      <canvas ref={chartRef} style={{ width: '100%', height: 'auto' }} />
    </div>
  );
}

appsmith.onReady(() => {
  ReactDOM.render(<Dashboard />, document.getElementById("root"));
});
